import React, { useState, useEffect } from "react";
import Header from "../components/Header/Header";
import Homepage from "../components/Homepage/Homepage";

export function Home() {
  const [isNavExpanded, setIsNavExpanded] = useState(false);
  const [isPageVisible, setIsPageVisible] = useState(false);

  useEffect(() => {
    setIsPageVisible(!isNavExpanded); // Set page visibility based on navigation state
  }, [isNavExpanded]);

  const toggleNav = () => {
    setIsNavExpanded(!isNavExpanded);
    setIsPageVisible(false); // Hide the page when toggling navigation
  };

  return (
    <div>
      <Header toggleNav={toggleNav} isNavExpanded={isNavExpanded} />
      {isPageVisible && <Homepage />}
    </div>
  );
}

export default Home;
