import React, { useState } from "react";
import Header from "../components/Header/Header";
import Homepage from "../components/Homepage/Homepage";

export function Home() {
  const [isNavExpanded, setIsNavExpanded] = useState(false);
  const [isPageVisible, setIsPageVisible] = useState(true); // Initialize page visibility to true

  const toggleNav = () => {
    setIsNavExpanded(!isNavExpanded);
    setIsPageVisible(true); // Set page visibility to true when toggling navigation
  };

  const handleHomeClick = () => {
    if (!isNavExpanded) {
      setIsPageVisible(true); // Show the page when home is clicked and navigation is not expanded
    }
  };

  return (
    <div>
      <Header toggleNav={toggleNav} isNavExpanded={isNavExpanded} />
      {!isNavExpanded && (isPageVisible || window.innerWidth >= 768) && (
        <Homepage />
      )}
    </div>
  );
}

export default Home;
