import React, { useState, useEffect } from "react";
import Header from "../components/Header/Header";
import Homepage from "../components/Homepage/Homepage";

export function Home() {
  const [isNavExpanded, setIsNavExpanded] = useState(false);
  const [isPageVisible, setIsPageVisible] = useState(true); // Initialize page visibility to true

  useEffect(() => {
    if (isNavExpanded) {
      setIsPageVisible(false); // Hide the page when navigation is expanded
    }
  }, [isNavExpanded]);

  const toggleNav = () => {
    setIsNavExpanded(!isNavExpanded);
    if (!isNavExpanded) {
      setIsPageVisible(true); // Show the page when toggling navigation off
    }
  };

  const handleHomeClick = () => {
    setIsPageVisible(!isNavExpanded); // Toggle page visibility when home is clicked
  };

  return (
    <div>
      <Header toggleNav={toggleNav} isNavExpanded={isNavExpanded} />
      {isPageVisible && <Homepage />}
    </div>
  );
}

export default Home;
